import React, { useState, useEffect } from "react";

function TrustRafeeq() {
  const [likes, setLikes] = useState(0); 

  useEffect(() => {
    const storedLikes = localStorage.getItem("trustRafeeqLikes");
    if (storedLikes) {
      setLikes(parseInt(storedLikes, 10)); 
    }
  }, []);

  const handleLike = () => {
    const newLikes = likes + 1;
    setLikes(newLikes);
    localStorage.setItem("trustRafeeqLikes", newLikes); 
  };

  return (
    <div style={styles.container}>
      <button onClick={handleLike} style={styles.button}>
        I Trust Rafeeq
      </button>
      <p style={styles.likeCount}>{likes} {likes === 1 ? "Trust" : "eople trust rafeeq"}</p>
    </div>
  );
}

const styles = {
  container: {
    textAlign: "center",
    margin: "20px",
  },
  button: {
    backgroundColor: "#007BFF",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "16px",
  },
  likeCount: {
    marginTop: "10px",
    fontSize: "14px",
    color: "#555",
  },
};

export default TrustRafeeq;
