import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faThumbsUp } from "@fortawesome/free-solid-svg-icons";

function LikeButton() {
  const [likes, setLikes] = useState(0);
  const [liked, setLiked] = useState(false);

  const handleLike = () => {
    setLiked(!liked);
    setLikes(liked ? likes - 1 : likes + 1); // إضافة أو إزالة اللايك
  };

  return (
    <div style={styles.container}>
      <button onClick={handleLike} style={liked ? styles.likedButton : styles.button}>
        <FontAwesomeIcon icon={faThumbsUp} style={styles.icon} />
        {liked ? " Liked" : " Like"}
      </button>
      <p style={styles.likeCount}>{likes} {likes === 1 ? "Like" : "Likes"}</p>
    </div>
  );
}

const styles = {
  container: {
    textAlign: "center",
    margin: "20px",
  },
  button: {
    backgroundColor: "#e0e0e0",
    color: "#000",
    border: "none",
    borderRadius: "5px",
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "16px",
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  likedButton: {
    backgroundColor: "#007BFF",
    color: "#fff",
    border: "none",
    borderRadius: "5px",
    padding: "10px 20px",
    cursor: "pointer",
    fontSize: "16px",
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  icon: {
    fontSize: "20px",
  },
  likeCount: {
    marginTop: "10px",
    fontSize: "14px",
    color: "#555",
  },
};

export default LikeButton;
