import React, { useState, useEffect } from "react";
import "./TrustRafeeq.css"; // Import the CSS file

function TrustRafeeq() {
  const [likes, setLikes] = useState(0);
  const [hasLiked, setHasLiked] = useState(false);

  useEffect(() => {
    // Get total likes from localStorage
    const storedLikes = localStorage.getItem("trustRafeeqLikes");
    if (storedLikes) {
      setLikes(parseInt(storedLikes, 10));
    }

    // Check if the user has already liked
    const userHasLiked = localStorage.getItem("userHasLiked");
    if (userHasLiked === "true") {
      setHasLiked(true);
    }
  }, []);

  const handleLike = () => {
    if (hasLiked) return; // Prevent multiple likes from the same user

    const newLikes = likes + 1;
    setLikes(newLikes);
    setHasLiked(true);

    // Store new likes count and mark user as having liked
    localStorage.setItem("trustRafeeqLikes", newLikes);
    localStorage.setItem("userHasLiked", "true");
  };

  return (
    <div className="container">
      <button onClick={handleLike} className="button" disabled={hasLiked}>
        {hasLiked ? "You Trust Rafeeq" : "I Trust Rafeeq"}
      </button>
      <p className="likeCount">{likes} {likes === 1 ? "Person Trust Rafeeq" : "People Trust Rafeeq"}</p>
    </div>
  );
}

export default TrustRafeeq;
