import React, { useState, useEffect } from "react";
import "./TrustRafeeq.css";  // Import the CSS file

function TrustRafeeq() {
  const [likes, setLikes] = useState(0); 

  useEffect(() => {
    const storedLikes = localStorage.getItem("trustRafeeqLikes");
    if (storedLikes) {
      setLikes(parseInt(storedLikes, 10)); 
    }
  }, []);

  const handleLike = () => {
    const newLikes = likes + 1;
    setLikes(newLikes);
    localStorage.setItem("trustRafeeqLikes", newLikes); 
  };

  return (
    <div className="container">
      <button onClick={handleLike} className="button">
        I Trust Rafeeq
      </button>
      <p className="likeCount">{likes} {likes === 1 ? "Trust" : " People Trusts Rafeeq"}</p>
    </div>
  );
}

export default TrustRafeeq;
